SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";
CREATE TABLE IF NOT EXISTS `#__cities` (
  `ct_id` int(11) NOT NULL AUTO_INCREMENT,
  `ct_name` char(200) NOT NULL,
  `ct_people_count` int(11) NOT NULL COMMENT 'Количество человек',
  `ct_people_year` int(11) NOT NULL COMMENT 'Год переписи населения',
  PRIMARY KEY (`ct_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4 ;


CREATE TABLE IF NOT EXISTS `#__cities_lang` (
  `ctl_id` int(11) NOT NULL AUTO_INCREMENT,
  `ctl_city` int(11) NOT NULL,
  `ctl_lang` char(7) NOT NULL,
  `ctl_name` char(200) NOT NULL,
  `ctl_article` mediumtext NOT NULL COMMENT 'Описание города',
  `ctl_metakey` text NOT NULL,
  `ctl_metadesc` text NOT NULL,
  PRIMARY KEY (`ctl_id`),
  KEY `ctl_city` (`ctl_city`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10 ;



CREATE TABLE IF NOT EXISTS `#__exhibitions` (
  `exb_id` int(11) NOT NULL AUTO_INCREMENT,
  `exb_city` int(11) NOT NULL COMMENT 'ID города',
  `exb_start` date NOT NULL COMMENT 'Дата начала выставки',
  `exb_end` date NOT NULL COMMENT 'Дата закрытия выставки',
  `exb_introimg` text NOT NULL COMMENT 'Вступительная картинка',
  `exb_fullimg` text NOT NULL COMMENT 'Полная картинка',
  `exb_created` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT 'Дата создания',
  `exb_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT 'Дата модификации',
  `exb_ordering` int(11) NOT NULL DEFAULT '0' COMMENT 'Для сортировки',
  PRIMARY KEY (`exb_id`),
  KEY `exb_city` (`exb_city`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=69 ;




CREATE TABLE IF NOT EXISTS `#__exhibitions_industries` (
  `exbi_id` int(11) NOT NULL AUTO_INCREMENT,
  `exbi_exhibition` int(11) NOT NULL,
  `exbi_industry` int(11) NOT NULL,
  PRIMARY KEY (`exbi_id`),
  KEY `exbi_exhibition` (`exbi_exhibition`,`exbi_industry`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=5 ;




CREATE TABLE IF NOT EXISTS `#__exhibitions_lang` (
  `exbl_id` int(11) NOT NULL AUTO_INCREMENT,
  `exbl_exhibition` int(11) NOT NULL,
  `exbl_lang` char(7) NOT NULL,
  `exbl_alias` char(200) NOT NULL,
  `exbl_name` char(200) NOT NULL,
  `exbl_introtext` mediumtext NOT NULL,
  `exbl_fulltext` mediumtext NOT NULL,
  `exbl_metakey` text NOT NULL,
  `exbl_metadesc` text NOT NULL,
  PRIMARY KEY (`exbl_id`),
  KEY `exbl_exhibition` (`exbl_exhibition`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=19 ;



CREATE TABLE IF NOT EXISTS `#__industries` (
  `ind_id` int(11) NOT NULL AUTO_INCREMENT,
  `ind_name` char(200) NOT NULL COMMENT 'Комментарий',
  PRIMARY KEY (`ind_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=14 ;




CREATE TABLE IF NOT EXISTS `#__industries_lang` (
  `indl_id` int(11) NOT NULL AUTO_INCREMENT,
  `indl_industry` int(11) NOT NULL,
  `indl_lang` char(7) NOT NULL,
  `indl_name` text NOT NULL,
  PRIMARY KEY (`indl_id`),
  KEY `indl_industry` (`indl_industry`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=40 ;



